import { useEffect, useState } from "react";
import React, { useHistory } from "react-router-dom"
import { useHttpClient } from "../Hooks/Http-Hook"
import "./Course.css"
import Home from './Home'
import CourseList from "./CourseList";
var course;
const Course=()=>{
    const history=useHistory();
    const [type,setType]=useState("");
    const [course,setcourse]=useState([]);
    //const {isLoading,error,sendRequest,clearError}=useHttpClient();
    
    // try{
    //     course=await sendRequest(
    //         `https://web-mini.herokuapp.com/course`,
    //         'GET',
    //         {'Content-Type':'application/json'}
    //         );
    // }
    // catch(err){
    //     console.log("course error\n"+err);
    // }
    const typeHandler=()=>{
            setcourse([
        {
            title:"course1",
            type:"Aptitude",
            link:"source video 1"
        },
        {
            title:"course 2",
            type:"techical",
            link:"source video 2"
        },
        {
            title:"course 3",
            type:"techical",
            link:"source video 2"   
        }
     ]);
    }
    
    return(
        <div style={{paddingTop:"5%"}}>
            <h1>COURSES YOU WANT</h1>
            <div class="btn-group">
            <button onClick={typeHandler}>Aptitude series</button>
            <button onClick={()=>{setType("T")}}>Technical series</button>
            <button onClick={()=>{setType("H")}}>HR series</button>
            </div>
            
            <CourseList items={course} type={type}/>
        </div>
    )
}

export default Course;
