    }

    useEffect(async()=>{
        document.body.style.background="none";
        try{
            const data=await sendRequest(
               `http://localhost:5000/drive`
               );
           console.log(data);
           setList(data);
       }
       catch(err){
           console.log("drive error\n"+err);
       }
    },[list])
    

    const form=()=>{
        history.push('/form-drive');
    } 

   
    
    return(
        <React.Fragment>
        <div>
        {error && <ErrorModal error={error} onClear={clearError}/>}
        {isLoading && <LoadingSpinner asOverlay/>}
        {!auth.isLogedIn && <h1 style={{paddingTop:"5%",textAlign:"center"}}> You dont have permission without login</h1>}
        {!auth.isLogedIn && <div>
        <h1>DRIVES YOU WANT</h1>
        {auth.isAdmin && <button className="tn1" onClick={form}>Add New Drive</button>}   
            <DriveList items={list}/>
            <h1>bye</h1>
        </div>}
        </div>
        </React.Fragment>

    );
}

export default Drive;
