import { useEffect } from "react";
import React, { useHistory } from "react-router-dom"
import { useHttpClient } from "../Hooks/Http-Hook"
import "./Course.css"
import CourseList from "./CourseList";
const Course=()=>{
    const history=useHistory();
    
    //const {isLoading,error,sendRequest,clearError}=useHttpClient();
    
    // try{
    //     course=await sendRequest(
    //         `https://web-mini.herokuapp.com/course`,
    //         'GET',
    //         {'Content-Type':'application/json'}
    //         );
    // }
    // catch(err){
    //     console.log("course error\n"+err);
    // }
    let type,course='';
    const typeHandler=()=>{
            course=[
        {
            title:"course1",
            type:"Aptitude",
            link:"source video 1"
        },
        {
            title:"course 2",
            type:"techical",
            link:"source video 2"
        },
        {
            title:"course 3",
            type:"techical",
            link:"source video 2"   
        }
     ]
     history.push('/courses')
    }
    useEffect(()=>{history.push('/courses')},[course])
    
    return(
        <div style={{paddingTop:"5%"}}>
            <h1>COURSES YOU WANT</h1>
            <div class="btn-group">
            <button onClick={typeHandler}>Aptitude series</button>
            <button onClick={()=>{type="T"}}>Technical series</button>
            <button onClick={()=>{type="H"}}>HR series</button>
            </div>
            
            <CourseList items={course} type={type}/>
        </div>
    )
}

export default Course;
