import React, { useState } from 'react';
import './DriveItem.css';

const DriveItem = props => {
   const [showl,setl]=useState(0);
   const show=()=>{
      if(showl===0){
      document.getElementById('lower').style.display='block';
      setl(1);
      }
      else{
         document.getElementById('lower').style.display='none';
      setl(0);
      }
   }

  return (

      <li className="car2">
         <div id='upper' class="upper-container"  >
           <div className="title_drive" onClick={show}>
              <h1>{props.name}</h1>
              </div> 
            
         </div>
         <div id='lower' class="lower-container">
            <div>
               <h3>{props.role}</h3>
               
            </div>
            <div>
               <p>{props.desc}</p>
               <h4>Last date for Apply is {props.date}</h4>
               <hr/>
               <br/>
            </div>
            <div>
               <br/>
               <a href={props.link} class="btn" target="_blank">Apply</a>
            </div>
         </div>
      </li>
    
  );
};

export default DriveItem;
