import { useState } from "react";
import React from "react-router-dom"
// import { useHttpClient } from "../Hooks/Http-Hook"
import "./Course.css"
import CourseList from "./CourseList";
const Course=()=>{
    const history=useHistory();
    const [type,setType]=useState("");
    const [course,setcourse]=useState([]);
    //const {isLoading,error,sendRequest,clearError}=useHttpClient();
    
    // try{
    //     course=await sendRequest(
    //         `https://web-mini.herokuapp.com/course`,
    //         'GET',
    //         {'Content-Type':'application/json'}
    //         );
    // }
    // catch(err){
    //     console.log("course error\n"+err);
    // }
    const typeHandler=()=>{
        if(type==="A")
            setcourse([
        {
            title:"course1",
            type:"Aptitude",
            link:"source video 1"
        },
        {
            title:"course 2",
            type:"techical",
            link:"source video 2"
        },
        {
            title:"course 3",
            type:"techical",
            link:"source video 2"   
        }
     ]);
     else{
        setcourse([
            {
                title:"course A1",
                type:"Aptitude",
                link:"source video 1"
            },
            {
                title:"course A2",
                type:"techical",
                link:"source video 2"
            },
            {
                title:"courseA 3",
                type:"techical",
                link:"source video 2"   
            }
         ]);
     }
    }
    
    return(
        <div style={{paddingTop:"5%"}}>
            <h1>COURSES YOU WANT</h1>
            <div class="btn-group">
            <button onClick={()=>{setType("A");typeHandler()}}>Aptitude series</button>
            <button onClick={()=>{setType("T");typeHandler()}}>Technical series</button>
            <button onClick={()=>{setType("H");typeHandler()}}>HR series</button>
            </div>
            
            <CourseList items={course}/>
        </div>
    )
}

export default Course;
